import { Component } from 'react';
import io from 'socket.io-client';
import { render } from 'react-dom';
import { Video } from '../components/Video';
// const Watcher = () => {
export default class Medium extends Component {
    constructor(props){
        super(props);

        this.state={
            hello:''
        }
    }

    componentDidMount(){
        this.socket=io()
        this.socket.emit('watcher');
        this.socket.on('now', (data) => {
            this.setState({
                hello: data.message
            });
        });

        const peerConnection = new RTCPeerConnection();
        this.socket.on('offer', message => {
            console.log("OFFER RECEIVED IN WATCHER");
            peerConnection.setRemoteDescription(message)
                .then(() => peerConnection.createAnswer())
                .then(sdp => peerConnection.setLocalDescription(sdp))
                .then(() => {
                    console.log("ANSWER: " + peerConnection.localDescription);
                    this.socket.emit('answer', peerConnection.localDescription);
                });
        });

        peerConnection.onaddstream = function (event) {
            video.srcObject = event.stream;
            console.log("VIDEO: " + video);
        };
        console.log("VIDEO: outside : " + video);
    }
    
    render(){
        return(
            <div>
                <h1>{this.state.hello}</h1>
                <video id="video" autoPlay>
                </video>
                <p>test</p>
            </div>
            
        )
    }
}
