import { Component } from 'react';
import io from 'socket.io-client';
import { render } from 'react-dom';

// const Broadcaster = () => {
export default class Medium extends Component {
    constructor(props){
        super(props);

        this.state={
            hello:''
        }
    }

    componentDidMount(){
        document.getElementById("test").innerHTML = `tf bro`;
        this.socket=io();
        this.socket.emit('broadcaster');
        this.socket.on('now', (data) => {
            this.setState({
                hello: data.message
            })
        })

        const peerConnections = {};
        const video = document.getElementById('video');
        const peerConnection = new RTCPeerConnection();
        navigator.mediaDevices.getUserMedia({video: true, audio: true})
            .then((stream) => {
                video.srcObject = stream;
                // stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
                // peerConnection.createOffer()
                //     .then(sdp => peerConnection.setLocalDescription(sdp))
                //     .then(() => {
                //         console.log("OFFER: " + peerConnection.localDescription);
                //         this.socket.emit('offer', peerConnection.localDescription);
                //     });
                this.socket.emit('broadcaster');
            })
            .catch(function (err) {
                console.log(err);
            });

        this.socket.on('answer', (id, description) => {
            peerConnections[id].setRemoteDescription(description);
        });

        this.socket.on('watcher', id => {
            const peerConnection = new RTCPeerConnection();
            peerConnections[id] = peerConnection;
            let stream = video.srcObject;
            stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
            peerConnection.createOffer()
            .then(sdp => peerConnection.setLocalDescription(sdp))
            .then( () => {
                this.socket.emit('offer', id, peerConnection.localDescription);
            });
        });

        navigator.mediaDevices.getUserMedia({ video: true, audio: true })
            .then((stream) => { video.srcObject = stream })
            .catch(function (err0r) {
                console.log("Something went wrong!");
            });
    }
    
    render(){
        return(
            <div>
                <h1>{this.state.hello}</h1>
                <video id="video" autoPlay>
                </video>
                <div id="test">
                </div>
            </div>
            
        )
    }
}
