    render(){
        return(
            <div>
                <form id="search">
                    <input type="text" id="data" name="data"/>
                    <button id="search_btn">
                        Search
                    </button>
                </form>
                <ul id="recipes">
                </ul>
            </div>
            
        )
    }
}
