import { render } from 'react-dom';
import Webcam from "react-webcam";

class WebcamCapture extends React.Component {
    render() {
      const videoConstraints = {
        facingMode: { exact: "environment" }
      };
  
      return <Webcam videoConstraints={videoConstraints} />;
    }
