export default class Medium extends Component {
    constructor(props){
        super(props);

        this.state={
            hello:''
        }
    }

    componentDidMount(){
        this.socket=io()
        this.socket.on('now', (data) => {
            this.setState({
                hello: data.message
            })
        })
    }
    
    render(){
        return(
            <div>
                <h1>{this.state.hello}</h1>
                <p>test</p>
            </div>
            
        )
    }
}

