import { Component } from 'react';
import io from 'socket.io-client';
import { render } from 'react-dom';
import Router from 'next/router';

const apiKey = '56c94cc84b534f349b59f11eb9d6ae51';

export default class RecipeWiki extends Component {
    componentDidMount(){
        const search = document.getElementById('search');
        search.addEventListener('submit', function(event){
            event.preventDefault();
            let data = document.querySelector('#data').value;
            document.getElementById('search').reset();
            console.log(data);

            const res = fetch('api/recipe')
                .then(function(response){
                    response.json().then(function(data){
                        console.log(data);
                        // resulting array of data (recipes)
                        let recipe = document.getElementById('recipes');
                        let baseUri = data.baseUri;
                        data.results.forEach(result => {
                            let imageUrl = result.image;
                            console.log(baseUri + imageUrl);
                            recipe.innerHTML = recipe.innerHTML + `
                            <div className="recipe" style="display:flex;flex-direction:row;margin-bottom:20px;border:2px solid #000;padding:5px;">
                                <img src="${baseUri + imageUrl}" className="recipeImage" height="200" style="padding-right:50px;"/>
                                <div className="recipeInfo" style="display:flex;flex-direction:column;">
                                    <div style="padding-bottom:68px;font-size:30px;;">${result.title}</div>
                                    <div style="padding-bottom:10px">Preparation time: ${result.readyInMinutes}</div>
                                    <div>Number of servings: ${result.servings}</div>
                                </div>
                            </div>

                        `;
                        });
                        // let test = document.getElementsByClassName('test');
                        // test.innerHtml = test.innerHtml + `
                        // <style>
                        //     {color: blue};
                        // </style>`
                    });
                });
        });
    }
    render(){
        return(
            <div className="main">
                <div className="body">
                    <form id="search" className="search">
                        <input type="text" id="data" name="data"/>
                        <button id="search_btn">
                            Search
                        </button>
                    </form>

                    <div id="recipes" className="recipes">
                    </div>
                </div>

                <style>{`
                .main{
                    font-family: 'SF Pro Text', 'SF Pro Icons', 'Helvetica Neue', 'Helvetica',
                    'Arial', sans-serif;
                    padding: 20px 20px 60px;
                    max-width: 680px;
                    margin: 0 auto;
                }
                .body {
                    display: flex;
                    flex-direction: column;
                }
                .search {
                    padding-bottom: 80px;
                    align-items: center;
                }
                `}
                </style>
            </div>
        )
    }
}
