import React, { useEffect, useState } from 'react';

const RecipeWiki = () => {
    const [recipe, setRecipe] = useState(null);
    useEffect(() => {
        async function getRecipe(){
            const res = await fetch('/api/recipe');
            const newRecipe = await res.json();
            setRecipe(newRecipe);
        }
        getRecipe();
    }, []);
    
    return (
        <div>
            <h1>{recipe}</h1>
        </div>
    );
}
export default RecipeWiki;
